import React, { useMemo } from 'react';
import courses from './data/courses';
import styles from './Courses.module.css';
import { Link, useLocation } from 'react-router-dom';

const Courses = () => {
  const location = useLocation();

  const sortCourses = (courses, sort) => {
    if (sort === 'title-asc') {
      return [...courses].sort((a, b) => a.title.localeCompare(b.title));
    } else if (sort === 'title-desc') {
      return [...courses].sort((a, b) => b.title.localeCompare(a.title));
    } else {
      return courses;
    }
  };

  const searchParams = new URLSearchParams(location.search);
  const sort = searchParams.get('sort');

  const sortedCourses = useMemo(() => sortCourses(courses, sort), [sort, courses]);

  return (
    <div className={styles.courses}>
      <h1>Courses</h1>
      <div>
        <label>Sort by: </label>
        <select
          value={sort || ''}
          onChange={(e) => {
            const sortParam = e.target.value;
            const newSearchParams = new URLSearchParams(location.search);
            if (sortParam) {
              newSearchParams.set('sort', sortParam);
            } else {
              newSearchParams.delete('sort');
            }
            window.location.search = newSearchParams.toString();
          }}
        >
          <option value="">None</option>
          <option value="title-asc">Title (A-Z)</option>
          <option value="title-desc">Title (Z-A)</option>
        </select>
      </div>
      <ul>
        {sortedCourses.map((course) => (
          <li key={course.id}>
            <p>{course.description}</p>
            <Link to={`/courses/${course.slug}`}>{course.title}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Courses;
