import React, {useEffect, useState} from 'react';
import courses from './data/courses';
import styles from './Courses.module.css';
import {Link, useLocation} from 'react-router-dom';
import queryString from 'query-string';

const Courses = () => {
 const location = useLocation();
const query = queryString.parse(location.search);
  const [sortedCourses, setSourtedCourses] = useState();

  const sortCourses = (courses, property) => {
    const sortedCourses = [...courses].sort((a, b) => {
      if (a[property] < b[property]) {
        return -1;
      } else if (a[property] > b[property]) {
        return 1;
      } else {
        return 0;
      }
    });
    return sortedCourses;
  };


useEffect(() => {
    setSourtedCourses(sortCourses(courses, query.sort));
}, [query.sort]);



  return (
    <div className={styles.courses}>
      <h1>Courses</h1>
      <div className={styles.sort}>
        <span>Sort by:</span>
        <Link to="/courses?sort=title">Title</Link>
        <Link to="/courses?sort=author">Author</Link>
      </div>
      <ul>
        {sortedCourses && sortedCourses.map((course) => (
          <li key={course.id}>
            <Link to={`/courses/${course.id}`}>{course.title}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
};
export default Courses;
